mapper.insert:
INSERT INTO flight_seats (seat_number, flight_id, seat_type)
SELECT seat_number, #{flightId} AS flight_id, seat_type
FROM seats;

mapper.update:
UPDATE flight_seats
SET is_sold = #{sold}
WHERE flight_id = #{flightId} AND seat_number = #{seatNumber} AND seat_type = #{seatType};

mapper.select:
SELECT flight_seat_id, seat_number, seat_type, is_sold
FROM flight_seats
WHERE flight_id = #{flightId} AND seat_type = 'B';

SELECT flight_seat_id, seat_number, seat_type, is_sold
FROM flight_seats
WHERE flight_id = #{flightId} AND seat_type = 'E';

select count(*)
from flight_seats
WHERE flight_id = #{flightId}
